import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { FaTimes, FaChevronLeft, FaChevronRight, FaHeart, FaCamera, FaPlay, FaExpand, FaDownload, FaRing } from 'react-icons/fa';
import './Gallery.css';

const Gallery = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);

  // Sample gallery images - you can replace these with actual photos
  const galleryImages = [
    {
      id: 1,
      src: 'https://images.unsplash.com/photo-1519741497674-611481863552?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      alt: 'Our First Date',
      category: 'dating',
      featured: true
    },
    {
      id: 2,
      src: 'https://images.unsplash.com/photo-1511285560929-80b456fea0bc?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      alt: 'College Days',
      category: 'dating',
      featured: true
    },
    {
      id: 3,
      src: 'https://images.unsplash.com/photo-1519741497674-611481863552?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      alt: 'Photography Class',
      category: 'dating'
    },
    {
      id: 4,
      src: 'https://images.unsplash.com/photo-1511285560929-80b456fea0bc?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      alt: 'Travel Adventures',
      category: 'dating'
    },
    {
      id: 5,
      src: 'https://images.unsplash.com/photo-1519741497674-611481863552?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      alt: 'The Proposal',
      category: 'engagement',
      featured: true
    },
    {
      id: 6,
      src: 'https://images.unsplash.com/photo-1511285560929-80b456fea0bc?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      alt: 'Engagement Photos',
      category: 'engagement'
    },
    {
      id: 7,
      src: 'https://images.unsplash.com/photo-1519741497674-611481863552?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      alt: 'Wedding Planning',
      category: 'engagement'
    },
    {
      id: 8,
      src: 'https://images.unsplash.com/photo-1511285560929-80b456fea0bc?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      alt: 'Our Journey',
      category: 'dating'
    }
  ];

  const openLightbox = (image, index) => {
    setSelectedImage(image);
    setCurrentIndex(index);
  };

  const closeLightbox = () => {
    setSelectedImage(null);
  };

  const goToNext = () => {
    const nextIndex = (currentIndex + 1) % galleryImages.length;
    setCurrentIndex(nextIndex);
    setSelectedImage(galleryImages[nextIndex]);
  };

  const goToPrevious = () => {
    const prevIndex = (currentIndex - 1 + galleryImages.length) % galleryImages.length;
    setCurrentIndex(prevIndex);
    setSelectedImage(galleryImages[prevIndex]);
  };

  return (
    <section className="gallery-section">
      <div className="container">
        <motion.div 
          className="section-header"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          viewport={{ once: true }}
        >
          <h2 className="section-title">Our Love Story</h2>
          <p className="section-subtitle">A journey through our most precious moments</p>
        </motion.div>

        {/* Featured Images Section */}
        <motion.div 
          className="featured-gallery"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
          viewport={{ once: true }}
        >
          <div className="featured-grid">
            {galleryImages.filter(img => img.featured).map((image, index) => (
              <motion.div
                key={image.id}
                className={`featured-item featured-${index + 1}`}
                onClick={() => openLightbox(image, galleryImages.indexOf(image))}
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                initial={{ opacity: 0, scale: 0.8 }}
                whileInView={{ opacity: 1, scale: 1 }}
                transition={{ duration: 0.6, delay: index * 0.2 }}
                viewport={{ once: true }}
              >
                <div className="image-container">
                  <img src={image.src} alt={image.alt} />
                  <div className="image-overlay">
                    <div className="overlay-content">
                      <FaExpand className="overlay-icon" />
                      <span className="overlay-text">{image.alt}</span>
                    </div>
                  </div>
                </div>
              </motion.div>
            ))}
          </div>
        </motion.div>

        {/* Timeline Section */}
        <motion.div 
          className="timeline-section"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.4 }}
          viewport={{ once: true }}
        >
          <div className="timeline-container">
            <div className="timeline-line"></div>
            
            <div className="timeline-item">
              <div className="timeline-marker">
                <FaHeart />
              </div>
              <div className="timeline-content">
                <h3>How We Met</h3>
                <p>College photography class brought us together. Keziah was the creative soul, Darwin the technical perfectionist. Our first assignment was to capture "beauty in the ordinary" - little did we know we'd find it in each other.</p>
                <div className="timeline-images">
                  {galleryImages.filter(img => img.category === 'dating').slice(0, 2).map((image, index) => (
                    <motion.div
                      key={image.id}
                      className="timeline-image"
                      onClick={() => openLightbox(image, galleryImages.indexOf(image))}
                      whileHover={{ scale: 1.1 }}
                      initial={{ opacity: 0, x: -20 }}
                      whileInView={{ opacity: 1, x: 0 }}
                      transition={{ duration: 0.5, delay: index * 0.1 }}
                      viewport={{ once: true }}
                    >
                      <img src={image.src} alt={image.alt} />
                    </motion.div>
                  ))}
                </div>
              </div>
            </div>

            <div className="timeline-item">
              <div className="timeline-marker">
                <FaRing />
              </div>
              <div className="timeline-content">
                <h3>The Proposal</h3>
                <p>Darwin planned the perfect proposal during a sunset photoshoot. As Keziah focused on capturing the golden hour, Darwin got down on one knee. The camera captured her surprise, and our photographer friend captured the moment she said yes.</p>
                <div className="timeline-images">
                  {galleryImages.filter(img => img.category === 'engagement').slice(0, 2).map((image, index) => (
                    <motion.div
                      key={image.id}
                      className="timeline-image"
                      onClick={() => openLightbox(image, galleryImages.indexOf(image))}
                      whileHover={{ scale: 1.1 }}
                      initial={{ opacity: 0, x: 20 }}
                      whileInView={{ opacity: 1, x: 0 }}
                      transition={{ duration: 0.5, delay: index * 0.1 }}
                      viewport={{ once: true }}
                    >
                      <img src={image.src} alt={image.alt} />
                    </motion.div>
                  ))}
                </div>
              </div>
            </div>
          </div>
        </motion.div>

        {/* Gallery Grid */}
        <motion.div 
          className="gallery-grid"
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          transition={{ duration: 0.8, delay: 0.6 }}
          viewport={{ once: true }}
        >
          {galleryImages.map((image, index) => (
            <motion.div
              key={image.id}
              className={`gallery-item ${image.featured ? 'featured' : ''}`}
              onClick={() => openLightbox(image, index)}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
              initial={{ opacity: 0, y: 30 }}
              whileInView={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.6, delay: index * 0.1 }}
              viewport={{ once: true }}
            >
              <div className="image-container">
                <img src={image.src} alt={image.alt} />
                <div className="image-overlay">
                  <div className="overlay-content">
                    <FaCamera className="overlay-icon" />
                    <span className="overlay-text">{image.alt}</span>
                  </div>
                </div>
              </div>
            </motion.div>
          ))}
        </motion.div>

        {/* Love Quote Section */}
        <motion.div 
          className="love-quote"
          initial={{ opacity: 0, y: 30 }}
          whileInView={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.8 }}
          viewport={{ once: true }}
        >
          <div className="quote-container">
            <div className="quote-icon">
              <FaHeart />
            </div>
            <blockquote>
              "In all the world, there is no heart for me like yours. In all the world, there is no love for you like mine."
            </blockquote>
            <cite>— Maya Angelou</cite>
          </div>
        </motion.div>
      </div>

      {/* Enhanced Lightbox Modal */}
      <AnimatePresence>
        {selectedImage && (
          <motion.div
            className="lightbox"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={closeLightbox}
          >
            <div className="lightbox-content" onClick={(e) => e.stopPropagation()}>
              <button className="lightbox-close" onClick={closeLightbox}>
                <FaTimes />
              </button>
              <button 
                className="lightbox-nav lightbox-prev" 
                onClick={goToPrevious}
                disabled={currentIndex === 0}
              >
                <FaChevronLeft />
              </button>
              <button 
                className="lightbox-nav lightbox-next" 
                onClick={goToNext}
                disabled={currentIndex === galleryImages.length - 1}
              >
                <FaChevronRight />
              </button>
              <div className="lightbox-image-container">
                <img src={selectedImage.src} alt={selectedImage.alt} />
              </div>
              <div className="lightbox-info">
                <h3>{selectedImage.alt}</h3>
                <p>{currentIndex + 1} of {galleryImages.length}</p>
                <div className="lightbox-actions">
                  <button className="action-btn">
                    <FaDownload />
                  </button>
                  <button className="action-btn">
                    <FaPlay />
                  </button>
                </div>
              </div>
            </div>
          </motion.div>
        )}
      </AnimatePresence>
    </section>
  );
};

export default Gallery;
