import React from 'react';
import { motion } from 'framer-motion';
import { Link } from 'react-router-dom';
import { FaHeart, FaCalendarAlt, FaMapMarkerAlt } from 'react-icons/fa';
import './LandingPage.css';

const LandingPage = () => {
  return (
    <div className="landing-page">
      {/* Background Image/Gradient */}
      <div className="background-container">
        <div className="background-overlay"></div>
      </div>

      {/* Content */}
      <div className="landing-content">
        <motion.div 
          className="hero-text"
          initial={{ opacity: 0, y: 50 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 1, delay: 0.5 }}
        >
          <h1 className="couple-names">
            <span className="name">Sarah</span>
            <span className="ampersand">&</span>
            <span className="name">Michael</span>
          </h1>
          
          <motion.div 
            className="wedding-date"
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.8, delay: 1 }}
          >
            <FaCalendarAlt className="date-icon" />
            <span>June 15, 2024</span>
          </motion.div>

          <motion.div 
            className="wedding-location"
            initial={{ opacity: 0, scale: 0.8 }}
            animate={{ opacity: 1, scale: 1 }}
            transition={{ duration: 0.8, delay: 1.2 }}
          >
            <FaMapMarkerAlt className="location-icon" />
            <span>Garden Manor, California</span>
          </motion.div>

          <motion.p 
            className="invitation-text"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            transition={{ duration: 1, delay: 1.5 }}
          >
            Together with their families, we invite you to celebrate our special day
          </motion.p>

          <motion.div 
            className="cta-buttons"
            initial={{ opacity: 0, y: 30 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.8, delay: 2 }}
          >
            <Link to="/details" className="cta-button primary">
              Wedding Details
            </Link>
            <Link to="/rsvp" className="cta-button secondary">
              RSVP Now
            </Link>
          </motion.div>
        </motion.div>

        {/* Floating Hearts Animation */}
        <div className="floating-hearts">
          {[...Array(6)].map((_, i) => (
            <motion.div
              key={i}
              className="floating-heart"
              initial={{ 
                opacity: 0,
                x: Math.random() * window.innerWidth,
                y: window.innerHeight + 50
              }}
              animate={{ 
                opacity: [0, 1, 0],
                y: -100,
                x: Math.random() * window.innerWidth
              }}
              transition={{
                duration: 8,
                repeat: Infinity,
                delay: i * 1.5,
                ease: "linear"
              }}
            >
              <FaHeart />
            </motion.div>
          ))}
        </div>
      </div>

      {/* Scroll Indicator */}
      <motion.div 
        className="scroll-indicator"
        initial={{ opacity: 0 }}
        animate={{ opacity: 1 }}
        transition={{ duration: 1, delay: 3 }}
      >
        <div className="scroll-arrow"></div>
        <span>Scroll to explore</span>
      </motion.div>
    </div>
  );
};

export default LandingPage;
