import React, { useState } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { FaTimes, FaChevronLeft, FaChevronRight, FaHeart, FaCamera, FaPlay, FaExpand, FaDownload } from 'react-icons/fa';
import './Gallery.css';

const Gallery = () => {
  const [selectedImage, setSelectedImage] = useState(null);
  const [currentIndex, setCurrentIndex] = useState(0);

  // Sample gallery images - you can replace these with actual photos
  const galleryImages = [
    {
      id: 1,
      src: 'https://images.unsplash.com/photo-1519741497674-611481863552?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      alt: 'Our First Date',
      category: 'dating',
      featured: true
    },
    {
      id: 2,
      src: 'https://images.unsplash.com/photo-1511285560929-80b456fea0bc?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      alt: 'College Days',
      category: 'dating',
      featured: true
    },
    {
      id: 3,
      src: 'https://images.unsplash.com/photo-1519741497674-611481863552?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      alt: 'Photography Class',
      category: 'dating'
    },
    {
      id: 4,
      src: 'https://images.unsplash.com/photo-1511285560929-80b456fea0bc?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      alt: 'Travel Adventures',
      category: 'dating'
    },
    {
      id: 5,
      src: 'https://images.unsplash.com/photo-1519741497674-611481863552?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      alt: 'The Proposal',
      category: 'engagement',
      featured: true
    },
    {
      id: 6,
      src: 'https://images.unsplash.com/photo-1511285560929-80b456fea0bc?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      alt: 'Engagement Photos',
      category: 'engagement'
    },
    {
      id: 7,
      src: 'https://images.unsplash.com/photo-1519741497674-611481863552?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      alt: 'Wedding Planning',
      category: 'engagement'
    },
    {
      id: 8,
      src: 'https://images.unsplash.com/photo-1511285560929-80b456fea0bc?ixlib=rb-4.0.3&auto=format&fit=crop&w=800&q=80',
      alt: 'Our Journey',
      category: 'dating'
    }
  ];

  const openLightbox = (image, index) => {
    setSelectedImage(image);
    setCurrentIndex(index);
  };

  const closeLightbox = () => {
    setSelectedImage(null);
  };

  const nextImage = () => {
    const nextIndex = (currentIndex + 1) % galleryImages.length;
    setCurrentIndex(nextIndex);
    setSelectedImage(galleryImages[nextIndex]);
  };

  const prevImage = () => {
    const prevIndex = (currentIndex - 1 + galleryImages.length) % galleryImages.length;
    setCurrentIndex(prevIndex);
    setSelectedImage(galleryImages[prevIndex]);
  };

  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.1
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 0.6 }
    }
  };

  return (
    <div className="gallery">
      <div className="section">
        <motion.div
          className="gallery-container"
          variants={containerVariants}
          initial="hidden"
          animate="visible"
        >
          <motion.div className="gallery-header" variants={itemVariants}>
            <h1 className="section-title">Our Love Story</h1>
            <p className="section-subtitle">Capturing moments of our journey together</p>
          </motion.div>

          <motion.div className="gallery-grid" variants={itemVariants}>
            {galleryImages.map((image, index) => (
              <motion.div
                key={image.id}
                className="gallery-item"
                variants={itemVariants}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
                onClick={() => openLightbox(image, index)}
              >
                <div className="image-container">
                  <img
                    src={image.src}
                    alt={image.alt}
                    className="gallery-image"
                    loading="lazy"
                  />
                  <div className="image-overlay">
                    <FaCamera className="overlay-icon" />
                  </div>
                </div>
              </motion.div>
            ))}
          </motion.div>

          {/* Love Story Section */}
          <motion.div className="love-story" variants={itemVariants}>
            <div className="story-card">
              <div className="story-icon">
                <FaHeart />
              </div>
              <h3>Our Story</h3>
              <p>
                Keziah and Darwin met in college during a photography class. What started as 
                a friendship over shared love for capturing beautiful moments, blossomed into 
                a beautiful romance. After five years of adventures, travels, and countless 
                memories, Darwin proposed during a sunset photoshoot in Napa Valley - the 
                same place where they'll be saying "I do" on their wedding day.
              </p>
            </div>
          </motion.div>
        </motion.div>
      </div>

      {/* Lightbox Modal */}
      <AnimatePresence>
        {selectedImage && (
          <motion.div
            className="lightbox"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            onClick={closeLightbox}
          >
            <motion.div
              className="lightbox-content"
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.8, opacity: 0 }}
              onClick={(e) => e.stopPropagation()}
            >
              <button className="lightbox-close" onClick={closeLightbox}>
                <FaTimes />
              </button>
              
              <button className="lightbox-nav prev" onClick={prevImage}>
                <FaChevronLeft />
              </button>
              
              <button className="lightbox-nav next" onClick={nextImage}>
                <FaChevronRight />
              </button>
              
              <img
                src={selectedImage.src}
                alt={selectedImage.alt}
                className="lightbox-image"
              />
              
              <div className="lightbox-info">
                <h4>{selectedImage.alt}</h4>
                <p>{currentIndex + 1} of {galleryImages.length}</p>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default Gallery;
