import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './PreLanding.css';

const PreLanding = () => {
  const navigate = useNavigate();
  const [showOverlay, setShowOverlay] = useState(true);

  const handleClick = () => {
    setShowOverlay(false);
    setTimeout(() => {
      navigate('/home');
    }, 300);
  };

  return (
    <div className="pre-landing" onClick={handleClick}>
      <div className="video-container">
        <video 
          className="landing-video" 
          autoPlay 
          muted 
          playsInline
          loop
        >
          <source src="/Landing.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
        
        {showOverlay && (
          <div className="invitation-overlay">
            <div className="invitation-content">
              <h1 className="invitation-title">Keziah & Darwin</h1>
              <div className="invitation-subtitle">
                <p className="subtitle-main">You're Invited</p>
                <p className="subtitle-secondary">Click anywhere to enter</p>
              </div>
              <div className="click-indicator">
                <div className="pulse-circle"></div>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default PreLanding;
