import React, { useState, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import './InvitationVideo.css';

const InvitationVideo = () => {
  const [videoEnded, setVideoEnded] = useState(false);
  const [showContinue, setShowContinue] = useState(false);
  const videoRef = useRef(null);
  const navigate = useNavigate();

  const handleVideoEnd = () => {
    setVideoEnded(true);
    // Small delay before showing continue message
    setTimeout(() => {
      setShowContinue(true);
    }, 500);
  };

  const handleClick = () => {
    if (showContinue) {
      navigate('/home');
    }
  };

  return (
    <div className="invitation-video" onClick={handleClick}>
      <div className="video-container">
        <video
          ref={videoRef}
          className="invitation-video-player"
          autoPlay
          muted
          playsInline
          onEnded={handleVideoEnd}
        >
          <source src="/TentativeInvitation.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      </div>

      {/* Continue Message */}
      <AnimatePresence>
        {showContinue && (
          <motion.div
            className="continue-overlay"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 1 }}
          >
            <motion.div
              className="continue-content"
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              transition={{ 
                duration: 0.8,
                type: "spring",
                stiffness: 200,
                damping: 20
              }}
            >
              <motion.p
                className="continue-text"
                initial={{ y: 30, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ delay: 0.3, duration: 0.8 }}
              >
                Click anywhere to enter our wedding
              </motion.p>

              <motion.div
                className="click-indicator"
                initial={{ scale: 0 }}
                animate={{ scale: 1 }}
                transition={{ delay: 0.6, duration: 0.5 }}
              >
                <motion.div
                  className="pulse-circle"
                  animate={{ 
                    scale: [1, 1.2, 1],
                    opacity: [0.7, 0.3, 0.7]
                  }}
                  transition={{ 
                    duration: 2,
                    repeat: Infinity,
                    ease: "easeInOut"
                  }}
                />
                <motion.div
                  className="pulse-circle"
                  animate={{ 
                    scale: [1, 1.4, 1],
                    opacity: [0.5, 0.1, 0.5]
                  }}
                  transition={{ 
                    duration: 2,
                    repeat: Infinity,
                    ease: "easeInOut",
                    delay: 0.5
                  }}
                />
              </motion.div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default InvitationVideo;
