import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './LandingPage.css';

const LandingPage = () => {
  const navigate = useNavigate();
  const videoRef = useRef(null);
  const [showOverlay, setShowOverlay] = useState(false);
  const [showStoryVideo, setShowStoryVideo] = useState(false);

  const handleVideoEnd = () => {
    setShowOverlay(true);
  };

  const handleClick = () => {
    // Skip the story video for now and go directly to home
    navigate('/home');

    // Commented out - will use when StoryVid is compressed
    // if (showOverlay && !showStoryVideo) {
    //   setShowOverlay(false);
    //   setShowStoryVideo(true);
    // }
  };

  React.useEffect(() => {
    if (showStoryVideo && videoRef.current) {
      console.log('Story video should play');
      console.log('Video element:', videoRef.current);
      console.log('Video readyState:', videoRef.current.readyState);
      console.log('Video src:', videoRef.current.currentSrc);

      // Give it a moment to load the new source
      setTimeout(() => {
        if (videoRef.current) {
          videoRef.current.play().catch(err => {
            console.error('Error playing story video:', err);
          });
        }
      }, 100);
    }
  }, [showStoryVideo]);

  const handleStoryVideoEnd = () => {
    navigate('/home');
  };

  return (
    <div className="landing-page" onClick={showOverlay ? handleClick : undefined}>
      <video 
        ref={videoRef}
        key={showStoryVideo ? 'story' : 'landing'}
        className="landing-video" 
        autoPlay 
        muted 
        playsInline
        onEnded={!showStoryVideo ? handleVideoEnd : handleStoryVideoEnd}
        onError={(e) => console.error('Video error:', e)}
        preload="auto"
      >
        {!showStoryVideo ? (
          <source src="/Landing.mp4" type="video/mp4" />
        ) : (
          <>
            <source src="/StoryVid.mov" type="video/quicktime" />
            <source src="/StoryVid.mp4" type="video/mp4" />
          </>
        )}
        Your browser does not support the video tag.
      </video>

      {showOverlay && (
        <div className="invitation-overlay">
          <div className="invitation-content">
            <p className="subtitle-main">You're Cordially Invited</p>
            <p className="click-text">Click anywhere to enter</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default LandingPage;
