import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import './LandingPage.css';

const LandingPage = () => {
  const navigate = useNavigate();
  const videoRef = useRef(null);
  const storyVideoRef = useRef(null);
  const [showOverlay, setShowOverlay] = useState(false);
  const [showStoryVideo, setShowStoryVideo] = useState(false);

  const handleVideoEnd = () => {
    setShowOverlay(true);
  };

  const handleClick = () => {
    if (showOverlay && !showStoryVideo) {
      setShowOverlay(false);
      setShowStoryVideo(true);
    }
  };

  React.useEffect(() => {
    if (showStoryVideo && storyVideoRef.current) {
      console.log('Story video should play');
      console.log('Video element:', storyVideoRef.current);
      console.log('Video readyState:', storyVideoRef.current.readyState);
      console.log('Video src:', storyVideoRef.current.currentSrc);

      storyVideoRef.current.play().catch(err => {
        console.error('Error playing story video:', err);
      });

      // Fallback: try to reload and play
      setTimeout(() => {
        if (storyVideoRef.current && storyVideoRef.current.paused) {
          console.log('Video still paused, trying to force play');
          storyVideoRef.current.load();
          storyVideoRef.current.play().catch(err => {
            console.error('Second play attempt failed:', err);
          });
        }
      }, 500);
    }
  }, [showStoryVideo]);

  const handleStoryVideoEnd = () => {
    navigate('/home');
  };

  return (
    <div className="landing-page" onClick={showOverlay ? handleClick : undefined}>
      {!showStoryVideo ? (
        <video 
          ref={videoRef}
          className="landing-video" 
          autoPlay 
          muted 
          playsInline
          onEnded={handleVideoEnd}
        >
          <source src="/Landing.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      ) : (
        <video 
          ref={storyVideoRef}
          className="landing-video" 
          autoPlay 
          muted 
          playsInline
          preload="auto"
          onEnded={handleStoryVideoEnd}
          onError={(e) => console.error('Video error:', e)}
        >
          <source src="/StoryVid.mov" type="video/quicktime" />
          <source src="/StoryVid.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      )}

      {showOverlay && (
        <div className="invitation-overlay">
          <div className="invitation-content">
            <p className="subtitle-main">You're Cordially Invited</p>
            <p className="click-text">Click anywhere to enter</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default LandingPage;
