import React, { useState, useRef } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { useNavigate } from 'react-router-dom';
import './PreLanding.css';

const PreLanding = () => {
  const [videoEnded, setVideoEnded] = useState(false);
  const [showInvitation, setShowInvitation] = useState(false);
  const videoRef = useRef(null);
  const navigate = useNavigate();

  const handleVideoEnd = () => {
    setVideoEnded(true);
    // Small delay before showing invitation message
    setTimeout(() => {
      setShowInvitation(true);
    }, 500);
  };

  const handleClick = () => {
    if (showInvitation) {
      navigate('/home');
    }
  };


  return (
    <div className="pre-landing" onClick={handleClick}>
      <div className="video-container">
        <video
          ref={videoRef}
          className="landing-video"
          autoPlay
          muted
          playsInline
          onEnded={handleVideoEnd}
          onClick={handleVideoClick}
        >
          <source src="/Landing.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
        
        {/* Video overlay with play/pause indicator */}
        {!videoEnded && (
          <div className="video-overlay">
            <div className="video-controls">
              <motion.div
                className="play-indicator"
                animate={{ scale: [1, 1.1, 1] }}
                transition={{ duration: 2, repeat: Infinity }}
              >
                <div className="play-icon">â–¶</div>
                <p>Click to pause/play</p>
              </motion.div>
            </div>
          </div>
        )}
      </div>

      {/* Invitation Message */}
      <AnimatePresence>
        {showInvitation && (
          <motion.div
            className="invitation-overlay"
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            transition={{ duration: 1 }}
          >
            <motion.div
              className="invitation-content"
              initial={{ scale: 0.8, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              transition={{ 
                duration: 0.8,
                type: "spring",
                stiffness: 200,
                damping: 20
              }}
            >
              <motion.h1
                className="invitation-title"
                initial={{ y: 50, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ delay: 0.3, duration: 0.8 }}
              >
                You are invited!
              </motion.h1>
              
              <motion.p
                className="invitation-subtitle"
                initial={{ y: 30, opacity: 0 }}
                animate={{ y: 0, opacity: 1 }}
                transition={{ delay: 0.6, duration: 0.8 }}
              >
                Click anywhere to continue
              </motion.p>

              <motion.div
                className="click-indicator"
                initial={{ scale: 0 }}
                animate={{ scale: 1 }}
                transition={{ delay: 1, duration: 0.5 }}
              >
                <motion.div
                  className="pulse-circle"
                  animate={{ 
                    scale: [1, 1.2, 1],
                    opacity: [0.7, 0.3, 0.7]
                  }}
                  transition={{ 
                    duration: 2,
                    repeat: Infinity,
                    ease: "easeInOut"
                  }}
                />
                <motion.div
                  className="pulse-circle"
                  animate={{ 
                    scale: [1, 1.4, 1],
                    opacity: [0.5, 0.1, 0.5]
                  }}
                  transition={{ 
                    duration: 2,
                    repeat: Infinity,
                    ease: "easeInOut",
                    delay: 0.5
                  }}
                />
              </motion.div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
};

export default PreLanding;
