import React, { useState, useRef, useEffect } from 'react';
import Navigation from './Navigation';

const Home = () => {
  const videoRef = useRef(null);
  const [hasPlayed, setHasPlayed] = useState(() => {
    // Check if video has already played in this session
    return sessionStorage.getItem('tentativeInvitationPlayed') === 'true';
  });

  useEffect(() => {
    const video = videoRef.current;
    
    const handleEnded = () => {
      video.pause();
      // Mark as played in session storage
      sessionStorage.setItem('tentativeInvitationPlayed', 'true');
      setHasPlayed(true);
    };

    if (video) {
      video.addEventListener('ended', handleEnded);
      
      // If it hasn't played yet, play it
      if (!hasPlayed) {
        video.play().catch(err => {
          console.log('Autoplay prevented:', err);
        });
      }
      
      return () => {
        video.removeEventListener('ended', handleEnded);
      };
    }
  }, [hasPlayed]);

  return (
    <>
      <Navigation />
      <div className="home-section">
        <video 
          ref={videoRef}
          className="invitation-video" 
          muted 
          playsInline
          preload="auto"
        >
          <source src="/TentativeInvitation.mp4" type="video/mp4" />
          Your browser does not support the video tag.
        </video>
      </div>
    </>
  );
};

export default Home;
