import React from 'react';
import { motion } from 'framer-motion';
import { 
  FaCalendarAlt, 
  FaClock, 
  FaMapMarkerAlt, 
  FaUtensils, 
  FaMusic,
  FaCamera,
  FaGift,
  FaCar
} from 'react-icons/fa';
import './WeddingDetails.css';

const WeddingDetails = () => {
  const containerVariants = {
    hidden: { opacity: 0 },
    visible: {
      opacity: 1,
      transition: {
        staggerChildren: 0.2
      }
    }
  };

  const itemVariants = {
    hidden: { opacity: 0, y: 30 },
    visible: {
      opacity: 1,
      y: 0,
      transition: { duration: 0.6 }
    }
  };

  const timeline = [
    {
      time: "3:00 PM",
      event: "Ceremony",
      location: "Garden Pavilion",
      description: "Join us as we say 'I do' in our beautiful garden ceremony"
    },
    {
      time: "4:00 PM",
      event: "Cocktail Hour",
      location: "Rose Garden",
      description: "Enjoy drinks and appetizers while we take photos"
    },
    {
      time: "5:30 PM",
      event: "Reception",
      location: "Grand Ballroom",
      description: "Dinner, dancing, and celebration"
    },
    {
      time: "11:00 PM",
      event: "Send-off",
      location: "Main Entrance",
      description: "Sparkler send-off to end our magical day"
    }
  ];

  const details = [
    {
      icon: <FaMapMarkerAlt />,
      title: "Venue",
      content: "Garden Manor Estate",
      subtitle: "123 Garden Way, Napa Valley, CA 94558"
    },
    {
      icon: <FaUtensils />,
      title: "Dress Code",
      content: "Cocktail Attire",
      subtitle: "Semi-formal with garden party vibes"
    },
    {
      icon: <FaGift />,
      title: "Registry",
      content: "Amazon & Bed Bath & Beyond",
      subtitle: "Your presence is the greatest gift, but if you'd like to give something, we're registered at..."
    },
    {
      icon: <FaCar />,
      title: "Parking",
      content: "Complimentary Valet",
      subtitle: "Free valet parking available at the main entrance"
    }
  ];

  return (
    <div className="wedding-details">
      <div className="section">
        <motion.div
          className="details-container"
          variants={containerVariants}
          initial="hidden"
          animate="visible"
        >
          <motion.div className="details-header" variants={itemVariants}>
            <h1 className="section-title">Wedding Details</h1>
            <p className="section-subtitle">Everything you need to know about our special day</p>
          </motion.div>

          {/* Main Event Info */}
          <motion.div className="main-event-info" variants={itemVariants}>
            <div className="event-card main-card">
              <div className="event-icon">
                <FaCalendarAlt />
              </div>
              <div className="event-content">
                <h3>Keziah & Darwin's Wedding</h3>
                <p className="event-date">Saturday, June 15, 2024</p>
                <p className="event-time">3:00 PM - 11:00 PM</p>
                <p className="event-location">
                  <FaMapMarkerAlt className="location-icon" />
                  Garden Manor Estate, Napa Valley, CA
                </p>
              </div>
            </div>
          </motion.div>

          {/* Timeline */}
          <motion.div className="timeline-section" variants={itemVariants}>
            <h2 className="timeline-title">Wedding Timeline</h2>
            <div className="timeline">
              {timeline.map((item, index) => (
                <motion.div 
                  key={index}
                  className="timeline-item"
                  variants={itemVariants}
                  whileHover={{ scale: 1.02 }}
                >
                  <div className="timeline-time">
                    <FaClock className="time-icon" />
                    <span>{item.time}</span>
                  </div>
                  <div className="timeline-content">
                    <h4>{item.event}</h4>
                    <p className="timeline-location">
                      <FaMapMarkerAlt className="location-icon" />
                      {item.location}
                    </p>
                    <p className="timeline-description">{item.description}</p>
                  </div>
                </motion.div>
              ))}
            </div>
          </motion.div>

          {/* Additional Details */}
          <motion.div className="additional-details" variants={itemVariants}>
            <h2 className="details-title">Important Information</h2>
            <div className="details-grid">
              {details.map((detail, index) => (
                <motion.div 
                  key={index}
                  className="detail-card"
                  variants={itemVariants}
                  whileHover={{ scale: 1.05 }}
                >
                  <div className="detail-icon">
                    {detail.icon}
                  </div>
                  <div className="detail-content">
                    <h4>{detail.title}</h4>
                    <p className="detail-main">{detail.content}</p>
                    <p className="detail-sub">{detail.subtitle}</p>
                  </div>
                </motion.div>
              ))}
            </div>
          </motion.div>

          {/* Special Notes */}
          <motion.div className="special-notes" variants={itemVariants}>
            <div className="notes-card">
              <h3>Special Notes</h3>
              <ul>
                <li>Weather permitting, the ceremony will be held outdoors in our beautiful garden</li>
                <li>We'll have a backup indoor ceremony space in case of rain</li>
                <li>Please let us know of any dietary restrictions when you RSVP</li>
                <li>Children are welcome! We'll have a kids' table with activities</li>
                <li>Don't forget to bring your dancing shoes!</li>
              </ul>
            </div>
          </motion.div>
        </motion.div>
      </div>
    </div>
  );
};

export default WeddingDetails;
